    const [text, setText] = useState([]);

    useEffect(() => {
        if (info || !text.length) {
            // handleInfo(info);
            handleInfo(info);
            return;
        }
        async function getInfo() {
            const page = await wiki().page(props.search);
            const content = await page.sections();
            console.log(content);
            setInfo(content);
        };
        getInfo();

    });
    function handleInfo(info) {
