        // console.log(titles);
        setText(text);
    };
    console.log(text)
    const cardText = text.map((text, i) => {
        return (
            <p key={i}>
                {text}
            </p>
        );
    })
    return (
        <>
            {cardText}
        </>
    )
}

export default Meaning;
