import React, { useEffect, useState } from 'react';
import wiki from 'wikijs';
import { getMajorArcana } from '../Helpers/getCards';

const Card = (props) => {
    const [imgUrl, setImgUrl] = useState(null);
    console.log(props)
    useEffect(() => {
        if (imgUrl) return;
        async function getImgUrl() {
            const url = await wiki().page(props.search);

            setImgUrl(url);
        };
        getWiki();
    });

    return (
        <>
            <div>
                <p></p>
                <h3>{props.name}</h3>
                <img src={imgUrl}></img>
                <p></p>
            </div>
        </>
    );
}

export default Card;
